<?phpclass Redes_model extends CI_Model {    //var $connection;    // Constructor    public function __construct() {        parent::__construct();    }    public function getByUser($id) {        $query = $this->db->query("SELECT * FROM  redes WHERE id_user = '" . $id . "'");        return $query->row_array();    }    public function add($data) {        $this->db->insert("redes", $data);    }    public function existeUser($id) {        $query = $this->db->query("SELECT * FROM  redes WHERE id_user = '" . $id . "'");        $query = $query->row_array();        if (isset($query['id']))            return true;        return FALSE;    }    public function update($data) {        $this->db->where('id_user', $data['id_user']);        $this->db->update('redes', array('drible' => $data['drible']));        $this->db->where('id_user', $data['id_user']);        $this->db->update('redes', array('facebook' => $data['facebook']));        $this->db->where('id_user', $data['id_user']);        $this->db->update('redes', array('google' => $data['google']));        $this->db->where('id_user', $data['id_user']);        $this->db->update('redes', array('instagram' => $data['instagram']));        $this->db->where('id_user', $data['id_user']);        $this->db->update('redes', array('linkedin' => $data['linkedin']));        $this->db->where('id_user', $data['id_user']);        $this->db->update('redes', array('twitter' => $data['twitter']));        $this->db->where('id_user', $data['id_user']);        $this->db->update('redes', array('youtube' => $data['youtube']));        $this->db->where('id_user', $data['id_user']);        $this->db->update('redes', array('pinterest' => $data['pinterest']));    }}